query:
// Node Creation
CREATE (StudentVisa:VisaType {Visa: "Student Visa"});
CREATE (GraduateVisa:VisaType {Visa: "Graduate Visa"});
CREATE (ChildStudentVisa:VisaType {Visa: "Child Student Visa"});
CREATE (SkilledWorkerVisa:VisaType {Visa: "Skilled Worker Visa"});
CREATE (MarriageVisa:VisaType {Visa: "Marriage Visa"});
CREATE (StandardVisitorVisa:VisaType {Visa: "Standard Visitor Visa"});
CREATE (TouristVisa:VisaType {Visa: "Standard Visitor Visa"});
CREATE (FamilyVisa:VisaType {Visa: "Family Visa"});
CREATE (BorderControl:BasicImmigrationLaws {Laws: "Border Control", Attributes: 'Compliance with UK entry requirements, Passport and visa validity, Declaration of goods'});
CREATE (TaxforIndividualsComingtotheUK:BasicImmigrationLaws {Laws: "Tax for Individuals Coming to the UK", Attributes: 'Tax residency rules, Income tax obligations'});

CREATE (Eligibility:StudentVisa {EligibilityCriteria: 'Must be 16 or over, Offer from a licensed sponsor, Financial resources, English language proficiency, Parental consent if under 18'});
CREATE (Eligibility:GraduateVisa {EligibilityCriteria: 'Completed a degree at a UK higher education provider, Valid Student Visa at time of application'});
CREATE (Eligibility:ChildStudentVisa {EligibilityCriteria: 'Must be between 4 to 17, Offer from licensed sponsor, Financial resources, Parental consent'});
CREATE (Eligibility:SkilledWorkerVisa {EligibilityCriteria: 'Job offer from a licensed sponsor, Salary meets required threshold, English language proficiency'});
CREATE (Eligibility:MarriageVisa {EligibilityCriteria: 'Must be married to a British citizen or settled person, Relationship must be genuine and subsisting, Adequate accommodation and financial support'});
CREATE (Eligibility:StandardVisitorVisa {EligibilityCriteria: 'Intention to visit the UK for tourism, business, or other permitted activities, Intention to leave at the end of the visit, Sufficient funds to cover expenses'});
CREATE (Eligibility:TouristVisa {EligibilityCriteria: 'Plan to visit the UK for tourism or leisure, Intent to leave at the end of the visit, Must meet the requirements of the standard visitor visa'});
CREATE (Eligibility:FamilyVisa {EligibilityCriteria: 'Must be a spouse, partner, child, parent, or other dependent relative of a British citizen or settled person, Relationship must be genuine and subsisting, Adequate accommodation and financial support'});

CREATE (Documents:StudentVisa {RequiredDocuments: 'Valid passport or travel document, Confirmation of Acceptance for Studies (CAS), Financial evidence, TB test certificate (if applicable)'});
CREATE (Documents:GraduateVisa {RequiredDocuments: 'Passport, CAS number, Proof of completion of degree)'});
CREATE (Documents:ChildStudentVisa {RequiredDocuments: 'Passport, Offer from licensed sponsor, Financial evidence, Parental consent letter'});
CREATE (Documents:SkilledWorkerVisa {RequiredDocuments: 'Certificate of Sponsorship (CoS), Passport, Proof of English language ability'});
CREATE (Documents:MarriageVisa {RequiredDocuments: 'Marriage certificate, Proof of relationship, Sponsor’s passport or proof of UK immigration status, Proof of income and accommodation'});
CREATE (Documents:StandardVisitorVisa {RequiredDocuments: 'Passport, Proof of funds, Travel itinerary, Letter of invitation (if applicable)'});
CREATE (Documents:TouristVisa {RequiredDocuments: 'Passport, Proof of funds, Travel itinerary, Hotel booking)'});
CREATE (Documents:FamilyVisa {RequiredDocuments: 'Proof of relationship, Sponsor’s passport or proof of UK immigration status, Proof of income and accommodation)'});

CREATE (Fees:StudentVisa {Fees: 'Application fee: £490, Health care surcharge which varies with visa duration)'});
CREATE (Fees:GraduateVisa {Fees: 'Application fee: £822, Immigration Health Surcharge'});
CREATE (Fees:ChildStudentVisa {Fees: 'Application fee: £490, Healthcare surcharge'});
CREATE (Fees:SkilledWorkerVisa {Fees: 'Up to 3 years: £719, More than 3 years: £1,420, Healthcare surcharge'});
CREATE (Fees:MarriageVisa {Fees: '£1,846 (if applying from outside the UK), £1,258 (if applying from inside the UK), Healthcare surcharge'});
CREATE (Fees:StandardVisitorVisa {Fees: '6 months: £115, 2 years: £432, 5 years: £771, 10 years: £963'});
CREATE (Fees:TouristVisa {Fees: 'Same as Standard Visitor Visa'});
CREATE (Fees:FamilyVisa {Fees: 'Applying Outside the UK: £1,846 (each person), Applying Inside the UK: £1,258 (each person)'});

CREATE (ProcessingTime:StudentVisa {ProcessingTime: 'Outside the UK: Usually within 3 weeks, Inside the UK: Usually within 8 weeks'});
CREATE (ProcessingTime:GraduateVisa {ProcessingTime: 'Up to 8 weeks'});
CREATE (ProcessingTime:ChildStudentVisa {ProcessingTime: '3 weeks'});
CREATE (ProcessingTime:SkilledWorkerVisa {ProcessingTime: 'Typically, 3 weeks'});
CREATE (ProcessingTime:MarriageVisa {ProcessingTime: 'Usually, 3 weeks'});
CREATE (ProcessingTime:StandardVisitorVisa {ProcessingTime: '3 weeks'});
CREATE (ProcessingTime:TouristVisa {ProcessingTime: 'Same as Standard Visitor Visa'});
CREATE (ProcessingTime:FamilyVisa {ProcessingTime: '12 weeks'});

CREATE (VisaLength:StudentVisa {VisaLength: 'Degree Level (18+): Up to 5 years, Below Degree Level: Up to 2 years'});
CREATE (VisaLength:GraduateVisa {VisaLength: '2 years (3 years for PhD graduates)'});
CREATE (VisaLength:ChildStudentVisa {VisaLength: 'Varies based on course length'});
CREATE (VisaLength:SkilledWorkerVisa {VisaLength: 'Up to 5 years'});
CREATE (VisaLength:MarriageVisa {VisaLength: '2.5 years'});
CREATE (VisaLength:StandardVisitorVisa {VisaLength: '6 months, 2 years, 5 years, or 10 years'});
CREATE (VisaLength:TouristVisa {VisaLength: 'Same as Standard Visitor Visa'});
CREATE (VisaLength:FamilyVisa {VisaLength: '2.5 years'});

CREATE (ApplicationProcess:StudentVisa {ApplicationProcess: 'Apply Online: How? Begin your application on the UK governments visa application website (usually through the Visa4UK service or the main GOV.UK visas section). Create an account to start the process. The online form will ask for personal information, travel details, and the purpose of your visit. Have your passport, previous travel history, and details of where you will stay ready before you begin filling the forms. Make sure the information is correct and as it appears on your passport. Answer questions related to your health, character, and any criminal history truthfully, Attend an appointment at a visa application centre, Pay the application fee (£490), Submit biometric information.'});
CREATE (ApplicationProcess:GraduateVisa {ApplicationProcess: 'Apply Online: How? Access the application form via the GOV.UK website. You will need to prove your identity. Be prepared to provide your CAS number, degree completion proof, and other supporting documents. Ensure you meet all eligibility requirements before starting the application, Pay the application fee.'});
CREATE (ApplicationProcess:ChildStudentVisa {ApplicationProcess: 'Apply Online: How? Apply through the GOV.UK website, ensuring that a parent or legal guardian completes the application on behalf of the child. You will need to provide the childs passport details, school acceptance letter, and financial information. Fill out personal, travel, and financial details accurately. Upload required documents such as the child’s birth certificate and parental consent letter to the online portal, Pay the application fee, Submit supporting documents.'});
CREATE (ApplicationProcess:SkilledWorkerVisa {ApplicationProcess: 'Apply Online: How? Access the application through the GOV.UK website. Secure your job offer and Certificate of Sponsorship first. Have your CoS, passport, and English language proficiency test results ready before you begin, Provide your biometric information, Ensure that you meet the eligibility requirements before beginning your application, Pay the application fee, Provide biometric information.'});
CREATE (ApplicationProcess:MarriageVisa {ApplicationProcess: 'Apply Online: How? Apply online via the GOV.UK website. The applicant must be outside the UK when applying. Have your marriage certificate, proof of relationship, and sponsors details ready before applying. Answer all questions honestly and provide any additional details that may be required, Provide your biometric information, Pay the application fee, Attend a biometric appointment.'});
CREATE (ApplicationProcess:StandardVisitorVisa {ApplicationProcess: 'Apply Online: How? Start by creating an account on the UK government visa application website. Enter your personal details, travel dates, and planned activities in the UK. Provide details of your income, savings, and how you plan to finance your trip. You will be required to upload a digital copy of your supporting documents to the online application portal. Use the link: https://visas- immigration.service.gov.uk/product/uk-visit-visa. All UK visa application forms must be completed online at www.Visa4UK.fco.gov.uk, Pay the application fee, Submit supporting documents.'});
CREATE (ApplicationProcess:TouristVisa {ApplicationProcess: 'Apply Online: How? Apply online through the standard visitor visa application portal on the GOV.UK website. Follow the same process as the Standard Visitor Visa. All UK visa application forms must be completed online at www.Visa4UK.fco.gov.uk. Have your passport, proof of funds, and travel itinerary ready. Answer all questions honestly and provide any additional details that may be required, Pay the application fee, Submit supporting documents.'});
CREATE (ApplicationProcess:FamilyVisa {ApplicationProcess: 'Apply Online: How? Apply online through the GOV.UK website. Complete the online application form with information such as the applicant’s personal details, immigration history, and reasons for applying for the visa. Provide evidence of the relationship, sponsor’s details, and financial information. Prepare all necessary documents and have them available for upload, Pay the application fee, Attend a biometric appointment.'});

//Relationship creation below

MATCH (vt:VisaType {Visa: 'Student Visa'}), (e_sv:StudentVisa {EligibilityCriteria: 'Must be 16 or over, Offer from a licensed sponsor, Financial resources, English language proficiency, Parental consent if under 18'})
MERGE (vt)-[:HAS_ELIGIBILITY]->(e_sv);
MATCH (vt:VisaType {Visa: 'Graduate Visa'}), (e_gv:GraduateVisa {EligibilityCriteria: 'Completed a degree at a UK higher education provider, Valid Student Visa at time of application'})
MERGE (vt)-[:HAS_ELIGIBILITY]->(e_gv);
MATCH (vt:VisaType {Visa: 'Child Student Visa'}), (e_cv:ChildStudentVisa {EligibilityCriteria: 'Must be between 4 to 17, Offer from licensed sponsor, Financial resources, Parental consent'})
MERGE (vt)-[:HAS_ELIGIBILITY]->(e_cv);
MATCH (vt:VisaType {Visa: 'Skilled Worker Visa'}), (e_wv:SkilledWorkerVisa {EligibilityCriteria: 'Job offer from a licensed sponsor, Salary meets required threshold, English language proficiency'})
MERGE (vt)-[:HAS_ELIGIBILITY]->(e_wv);
MATCH (vt:VisaType {Visa: 'Marriage Visa'}), (e_mv:MarriageVisa {EligibilityCriteria: 'Must be married to a British citizen or settled person, Relationship must be genuine and subsisting, Adequate accommodation and financial support'})
MERGE (vt)-[:HAS_ELIGIBILITY]->(e_mv);
MATCH (vt:VisaType {Visa: 'Standard Visitor Visa'}), (e_stv:StandardVisitorVisa {EligibilityCriteria: 'Intention to visit the UK for tourism, business, or other permitted activities, Intention to leave at the end of the visit, Sufficient funds to cover expenses'})
MERGE (vt)-[:HAS_ELIGIBILITY]->(e_stv);
MATCH (vt:VisaType {Visa: 'Standard Visitor Visa'}), (e_tv:TouristVisa {EligibilityCriteria: 'Plan to visit the UK for tourism or leisure, Intent to leave at the end of the visit, Must meet the requirements of the standard visitor visa'})
MERGE (vt)-[:HAS_ELIGIBILITY]->(e_tv);
MATCH (vt:VisaType {Visa: 'Family Visa'}), (e_fv:FamilyVisa {EligibilityCriteria: 'Must be a spouse, partner, child, parent, or other dependent relative of a British citizen or settled person, Relationship must be genuine and subsisting, Adequate accommodation and financial support'})
MERGE (vt)-[:HAS_ELIGIBILITY]->(e_fv);

MATCH (vt:VisaType {Visa: 'Student Visa'}), (d_sv:StudentVisa {RequiredDocuments: 'Valid passport or travel document, Confirmation of Acceptance for Studies (CAS), Financial evidence, TB test certificate (if applicable)'})
MERGE (vt)-[:REQUIRES_DOCUMENTS]->(d_sv);
MATCH (vt:VisaType {Visa: 'Graduate Visa'}), (d_gv:GraduateVisa {RequiredDocuments: 'Passport, CAS number, Proof of completion of degree)'})
MERGE (vt)-[:REQUIRES_DOCUMENTS]->(d_gv);
MATCH (vt:VisaType {Visa: 'Child Student Visa'}), (d_cv:ChildStudentVisa {RequiredDocuments: 'Passport, Offer from licensed sponsor, Financial evidence, Parental consent letter'})
MERGE (vt)-[:REQUIRES_DOCUMENTS]->(d_cv);
MATCH (vt:VisaType {Visa: 'Skilled Worker Visa'}), (d_wv:SkilledWorkerVisa {RequiredDocuments: 'Certificate of Sponsorship (CoS), Passport, Proof of English language ability'})
MERGE (vt)-[:REQUIRES_DOCUMENTS]->(d_wv);
MATCH (vt:VisaType {Visa: 'Marriage Visa'}), (d_mv:MarriageVisa {RequiredDocuments: 'Marriage certificate, Proof of relationship, Sponsor’s passport or proof of UK immigration status, Proof of income and accommodation'})
MERGE (vt)-[:REQUIRES_DOCUMENTS]->(d_mv);
MATCH (vt:VisaType {Visa: 'Standard Visitor Visa'}), (d_stv:StandardVisitorVisa {RequiredDocuments: 'Passport, Proof of funds, Travel itinerary, Letter of invitation (if applicable)'})
MERGE (vt)-[:REQUIRES_DOCUMENTS]->(d_stv);
MATCH (vt:VisaType {Visa: 'Standard Visitor Visa'}), (d_tv:TouristVisa {RequiredDocuments: 'Passport, Proof of funds, Travel itinerary, Hotel booking)'})
MERGE (vt)-[:REQUIRES_DOCUMENTS]->(d_tv);
MATCH (vt:VisaType {Visa: 'Family Visa'}), (d_fv:FamilyVisa {RequiredDocuments: 'Proof of relationship, Sponsor’s passport or proof of UK immigration status, Proof of income and accommodation)'})
MERGE (vt)-[:REQUIRES_DOCUMENTS]->(d_fv);

MATCH (vt:VisaType {Visa: 'Student Visa'}), (f_sv:StudentVisa {Fees: 'Application fee: £490, Health care surcharge which varies with visa duration)'})
MERGE (vt)-[:HAS_FEES]->(f_sv);
MATCH (vt:VisaType {Visa: 'Graduate Visa'}), (f_gv:GraduateVisa {Fees: 'Application fee: £822, Immigration Health Surcharge'})
MERGE (vt)-[:HAS_FEES]->(f_gv);
MATCH (vt:VisaType {Visa: 'Child Student Visa'}), (f_cv:ChildStudentVisa {Fees: 'Application fee: £490, Healthcare surcharge'})
MERGE (vt)-[:HAS_FEES]->(f_cv);
MATCH (vt:VisaType {Visa: 'Skilled Worker Visa'}), (f_wv:SkilledWorkerVisa {Fees: 'Up to 3 years: £719, More than 3 years: £1,420, Healthcare surcharge'})
MERGE (vt)-[:HAS_FEES]->(f_wv);
