MERGE (vt)-[:HAS_FEES]->(f_stv);
MATCH (vt:VisaType {Visa: 'Standard Visitor Visa'}), (f_tv:TouristVisa {Fees: 'Same as Standard Visitor Visa'})
MERGE (vt)-[:HAS_FEES]->(f_tv);
MATCH (vt:VisaType {Visa: 'Family Visa'}), (f_fv:FamilyVisa {Fees: 'Applying Outside the UK: £1,846 (each person), Applying Inside the UK: £1,258 (each person)'})
MERGE (vt)-[:HAS_FEES]->(f_fv);

MATCH (vt:VisaType {Visa: 'Student Visa'}), (pt_sv:StudentVisa {ProcessingTime: 'Outside the UK: Usually within 3 weeks, Inside the UK: Usually within 8 weeks'})
MERGE (vt)-[:HAS_PROCESSING_TIME]->(pt_sv);
MATCH (vt:VisaType {Visa: 'Graduate Visa'}), (pt_gv:GraduateVisa {ProcessingTime: 'Up to 8 weeks'})
MERGE (vt)-[:HAS_PROCESSING_TIME]->(pt_gv);
MATCH (vt:VisaType {Visa: 'Child Student Visa'}), (pt_cv:ChildStudentVisa {ProcessingTime: '3 weeks'})
MERGE (vt)-[:HAS_PROCESSING_TIME]->(pt_cv);
MATCH (vt:VisaType {Visa: 'Skilled Worker Visa'}), (pt_wv:SkilledWorkerVisa {ProcessingTime: 'Typically, 3 weeks'})
MERGE (vt)-[:HAS_PROCESSING_TIME]->(pt_wv);
MATCH (vt:VisaType {Visa: 'Marriage Visa'}), (pt_mv:MarriageVisa {ProcessingTime: 'Usually, 3 weeks'})
MERGE (vt)-[:HAS_PROCESSING_TIME]->(pt_mv);
MATCH (vt:VisaType {Visa: 'Standard Visitor Visa'}), (pt_stv:StandardVisitorVisa {ProcessingTime: '3 weeks'})
MERGE (vt)-[:HAS_PROCESSING_TIME]->(pt_stv);
MATCH (vt:VisaType {Visa: 'Standard Visitor Visa'}), (pt_tv:TouristVisa {ProcessingTime: 'Same as Standard Visitor Visa'})
MERGE (vt)-[:HAS_PROCESSING_TIME]->(pt_tv);
MATCH (vt:VisaType {Visa: 'Family Visa'}), (pt_fv:FamilyVisa {ProcessingTime: '12 weeks'})
MERGE (vt)-[:HAS_PROCESSING_TIME]->(pt_fv);

MATCH (vt:VisaType {Visa: 'Student Visa'}), (vl_sv:StudentVisa {VisaLength: 'Degree Level (18+): Up to 5 years, Below Degree Level: Up to 2 years'})
MERGE (vt)-[:HAS_VISA_LENGTH]->(vl_sv);
MATCH (vt:VisaType {Visa: 'Graduate Visa'}), (vl_gv:GraduateVisa {VisaLength: '2 years (3 years for PhD graduates)'})
MERGE (vt)-[:HAS_VISA_LENGTH]->(vl_gv);
MATCH (vt:VisaType {Visa: 'Child Student Visa'}), (vl_cv:ChildStudentVisa {VisaLength: 'Varies based on course length'})
MERGE (vt)-[:HAS_VISA_LENGTH]->(vl_cv);
MATCH (vt:VisaType {Visa: 'Skilled Worker Visa'}), (vl_wv:SkilledWorkerVisa {VisaLength: 'Up to 5 years'})
MERGE (vt)-[:HAS_VISA_LENGTH]->(vl_wv);
