MERGE (vt)-[:HAS_VISA_LENGTH]->(vl_mv);
MATCH (vt:VisaType {Visa: 'Standard Visitor Visa'}), (vl_stv:StandardVisitorVisa {VisaLength: '6 months, 2 years, 5 years, or 10 years'})
MERGE (vt)-[:HAS_VISA_LENGTH]->(vl_stv);
