MATCH (il:BasicImmigrationLaws {Laws: 'Border Control', Attributes: 'Compliance with UK entry requirements, Passport and visa validity, Declaration of goods'}), (ti:BasicImmigrationLaws {Laws: 'Tax for Individuals Coming to the UK', Attributes: 'Tax residency rules, Income tax obligations'})
MERGE (il)-[:COVERS]->(ti);

MATCH (vt:VisaType {Visa: 'Student Visa'}), (gv:VisaType {Visa: 'Graduate Visa'}), (cv:VisaType {Visa: 'Child Student Visa'}), (wv:VisaType {Visa: 'Skilled Worker Visa'}), (mv:VisaType {Visa: 'Marriage Visa'}), (stv:VisaType {Visa: 'Standard Visitor Visa'}), (tv:VisaType {Visa: 'Standard Visitor Visa'}), (fv:VisaType {Visa: 'Family Visa'})

RETURN *
